ON DUPLICATE KEY UPDATE
    name = IF(name <> VALUES(name), VALUES(name), name),
    tier = IF(tier <> VALUES(tier), VALUES(tier), tier),
    class_type = IF(class_type <> VALUES(class_type), VALUES(class_type), class_type),
    grade = IF(grade <> VALUES(grade), VALUES(grade), grade),
    icon = IF(icon <> VALUES(icon), VALUES(icon), icon);